package ramling; 
import java.util.Random;
public class MobileNo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mob=9;
		Random r=new Random();
		int m=r.nextInt(999999999);
		System.out.print("The mobile number generated is :");
		System.out.print(mob);
		System.out.print(m);

	}

}
