package ramling;
import java.util.Random;
public class P14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Random random=new Random();
		int a,b;
		a=random.nextInt();
		b=random.nextInt();
		System.out.println("The randomly generated integer values are");
		System.out.println(a);
		System.out.println(b);
		double c,d;
		c=random.nextDouble();
		d=random.nextDouble();
		System.out.println("The randomly generated double values are");
		System.out.println(c);
		System.out.println(d);
		long e,f;
		e=random.nextLong();
		f=random.nextLong();
		System.out.println("The randomly generated long values are");
		System.out.println(e);
		System.out.println(f);
		boolean g,h;
		g=random.nextBoolean();
		h=random.nextBoolean();
		System.out.println("The randomly generated boolean values are");
		System.out.println(g);
		System.out.println(h);
		
		
		
		

	}

}
